            s_n1=dup(ans);
            s_r2=dup(ans);
            s_d =dup(ans);
            s_x1=dup(ans);
            s_x2=dup(ans);
            s_b =dup(ans);
            s_n =dup(ans);
            s_i =dup(ans);
            s_rm=dup(ans);
            s_q =dup(ans);
            s_a =dup(ans);
